mapper.update:
update sys_itemsdetail
set deleteMark  = 1,deleteUserId =#{deleteUserId,jdbcType=VARCHAR} ,
deleteTime =#{deleteTime,jdbcType=TIMESTAMP}
where id=#{itemsId,jdbcType=VARCHAR}

mapper.select:
    select 

    from sys_itemsdetail 

    	deleteMark = 0 and itemId = #{itemId,jdbcType=VARCHAR}

				 and (itemName like ('%'+#{keyword}+'%') or itemCode like
				('%'+#{keyword}+'%') )




	   order by  ${sidx}  ${sord}

SELECT d.F_Id,d.F_ItemName FROM sys_itemsdetail d INNER JOIN Sys_Items i ON i.F_Id = d.F_ItemId WHERE 1 = 1
AND i.F_EnCode = #{enCode}
AND d.F_EnabledMark = 1 AND d.F_DeleteMark = 0 ORDER BY d.F_SortCode ASC
