mapper.select:
    select 

    from sys_itemsdetail 

    	F_DeleteMark = 0 and F_ItemId = #{itemId,jdbcType=VARCHAR}

				 and (F_ItemName like ('%'+#{keyword}+'%') or F_ItemCode like
				('%'+#{keyword}+'%') )




	   order by  ${sidx}  ${sord}

SELECT d.F_Id,d.F_ItemName FROM sys_itemsdetail d INNER JOIN Sys_Items i ON i.F_Id = d.F_ItemId WHERE 1 = 1
AND i.F_EnCode = #{enCode}
AND d.F_EnabledMark = 1 AND d.F_DeleteMark = 0 ORDER BY d.F_SortCode ASC
